                        <button class="bg-third border to-center" onclick="navButtonClick(this)">
                            <i class="bi bi-house-fill"></i>
                        </button>
                    </li>
                    <li class="nav-item">
                        <button class="bg-third border to-center" onclick="navButtonClick(this)">
